import randomfrom aiogram import types, Dispatcherfrom my_bots.bot2.venv.config import bot, ADMINasync def pin(message: types.Message):    if message.chat.type == "supergroup":        if not message.from_user.id in ADMIN:            await bot.pin_chat_message(message.chat.id, message.message_id)            #await message.answer('–≠—Ç–∞ –∫–æ–º–∞–Ω–¥–∞ –¥–æ—Å—Ç—É–ø–Ω–∞ —Ç–æ–ª—å–∫–∞ –∞–¥–º–∏–Ω–∞–º')        elif not message.reply_to_message:            await message.answer('–ö–æ–º–∞–Ω–¥–∞ –¥–æ–ª–∂–Ω–∞ –±—ã—Ç—å –æ—Ç–≤–µ—Ç–æ–º –Ω–∞ —Å–æ–æ–±—â–µ–Ω–∏–µ')        else:            await bot.pin_chat_message(message.chat.id, message.message_id)    else:        await message.answer('–≠—Ç–∞ –∫–æ–º–∞–Ω–¥–∞ –¥–æ—Å—Ç—É–ø–Ω–∞ —Ç–æ–ª—å–∫–æ –≤ –≥—Ä—É–ø–ø–∞—Ö(—á–∞—Ç–∞—Ö)')async def game(message: types.Message):    if message.chat.type == "supergroup":        if message.from_user.id in ADMIN:            if message.text[0:] == '/game':                animation_emoji = ['üèÄ', '‚öΩ', 'üé∞', 'üé≥', 'üéØ', 'üé≤']                random_emoji = random.choice(animation_emoji)                await bot.send_dice(message.chat.id, emoji=random_emoji)        else:            await message.answer('–≠—Ç–∞ –∫–æ–º–∞–Ω–¥–∞ –¥–æ—Å—Ç—É–ø–Ω–∞ —Ç–æ–ª—å–∫–æ –¥–ª—è –∞–¥–º–∏–Ω–æ–≤')    else:        await message.answer('–≠—Ç–∞ –∫–æ–º–∞–Ω–¥–∞ –¥–æ—Å—Ç—É–ø–Ω–∞ —Ç–æ–ª—å–∫–æ –≤ –≥—Ä—É–ø–ø–∞—Ö(—á–∞—Ç–∞—Ö)')async def ban(message: types.Message):    if message.chat.type == "supergroup":        if not message.from_user.id in ADMIN:            await message.answer('–≠—Ç–∞ –∫–æ–º–∞–Ω–¥–∞ –¥–æ—Å—Ç—É–ø–Ω–∞ —Ç–æ–ª—å–∫–∞ –∞–¥–º–∏–Ω–∞–º')        elif not message.reply_to_message:            await message.answer('–ö–æ–º–∞–Ω–¥–∞ –¥–æ–ª–∂–Ω–∞ –±—ã—Ç—å –æ—Ç–≤–µ—Ç–æ–º –Ω–∞ —Å–æ–æ–±—â–µ–Ω–∏–µ')        else:            await bot.kick_chat_member(message.chat.id, message.reply_to_message.from_user.id)            await message.answer(f'{message.from_user.first_name} –∑–∞–±–∞–Ω–∏–ª –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è\n'                                 f'{message.reply_to_message.from_user.id}')    else:        await message.answer('–≠—Ç–∞ –∫–æ–º–∞–Ω–¥–∞ –¥–æ—Å—Ç—É–ø–Ω–∞ —Ç–æ–ª—å–∫–æ –≤ –≥—Ä—É–ø–ø–∞—Ö(—á–∞—Ç–∞—Ö)')async def dice(message: types.Message):    if message.chat.type == "supergroup":        if message.from_user.id in ADMIN:            dice_client = await bot.send_dice(message.chat.id)            result_client = dice_client.dice.value      #class <int>            dice_bot = await bot.send_dice(message.chat.id)            result_bot = dice_bot.dice.value        #class <int>            results = f'–í–∞—à–∞ –∫—É–±–∏–∫ –≤—ã–ø–∞–ª –Ω–∞ {result_client} –∏ –∫—É–±–∏–∫ –±–æ—Ç–∞ –≤—ã–ø–∞–ª –Ω–∞ {result_bot}'            if result_client > result_bot:                await bot.send_message(message.chat.id, f'–ü–æ–∑–¥—Ä–∞–≤–ª—è–µ–º –≤—ã –≤—ã–∏–≥—Ä–∞–ª–∏\n'                                                                f'{results}')            elif result_client == result_bot:                await bot.send_message(message.chat.id, f'–ù–∏—á—å—è\n'                                                                f'{results}')            else:                await bot.send_message(message.chat.id, f'–í—ã –ø—Ä–æ–∏–≥—Ä–∞–ª–∏ –±–æ—Ç –≤—ã–∏–≥—Ä–∞–ª\n'                                                                f'{results}')        else:            await message.answer('–≠—Ç–∞ –∫–æ–º–∞–Ω–¥–∞ –¥–æ—Å—Ç—É–ø–Ω–∞ —Ç–æ–ª—å–∫–∞ –∞–¥–º–∏–Ω–∞–º')    else:        await message.answer('–≠—Ç–∞ –∫–æ–º–∞–Ω–¥–∞ –¥–æ—Å—Ç—É–ø–Ω–∞ —Ç–æ–ª—å–∫–æ –≤ –≥—Ä—É–ø–ø–∞—Ö(—á–∞—Ç–∞—Ö)')def register_handlers_admin(dp: Dispatcher):    dp.register_message_handler(ban, commands=['ban'])    dp.register_message_handler(pin, commands=['pin'], commands_prefix='/!')    dp.register_message_handler(game, commands=['game'])    dp.register_message_handler(dice, commands=['dice'])