from aiogram import types, Dispatcherfrom aiogram.types import InlineKeyboardButton, InlineKeyboardMarkupfrom my_bots.bot2.config import botasync def quiz_2(call: types.CallbackQuery):    markup2 = InlineKeyboardMarkup()    button_call_1 = InlineKeyboardButton("NEXT", callback_data='button_call_2')    markup2.add(button_call_1)# async def quiz_2(call: types.CallbackQuery):#     markup2 = InlineKeyboardMarkup()#     button_call_2 = InlineKeyboardButton("NEXT", callback_data='button_call_2')#     markup2.add(button_call_2)    question = 'Что выведет этот код?'    answers = [        'Hi wo2ld',        'Hi2world',        'Hi world',        'Error'    ]    test2 = open('media/py_tests/photo_2022-10-20_23-02-35.jpg', 'rb')    await bot.send_photo(call.from_user.id, photo=test2)    await bot.send_poll(        chat_id=call.from_user.id,        question=question,        options=answers,        is_anonymous=False,        type='quiz',        correct_option_id=2,        reply_markup=markup2    )async def quiz_3(call: types.CallbackQuery):    markup3 = InlineKeyboardMarkup()    button_call_2 = InlineKeyboardButton('NEXT', callback_data='button_call_3')    markup3.add(button_call_2)    question = 'Что выведет этот код?'    answers = [        '123',        '1 2 3',        '[1,2,3]',        'Error'    ]    test3 = open('media/py_tests/photo_2022-10-20_22-24-03.jpg', 'rb')    await bot.send_photo(call.from_user.id, photo=test3)    await bot.send_poll(        chat_id=call.from_user.id,        question=question,        options=answers,        is_anonymous=False,        type='quiz',        correct_option_id=3,        reply_markup=markup3    )async def quiz_4(call: types.CallbackQuery):    markup4 = InlineKeyboardMarkup()    button_call_3 = InlineKeyboardButton('NEXT', callback_data='button_call_4')    markup4.add(button_call_3)    question = 'Что выведет этот код?'    answers = [        '5',        '3',        'Error',        'None',        'Ошибку'    ]    test4 = open('media/py_tests/photo_2022-10-20_23-08-21.jpg', 'rb')    await bot.send_photo(call.from_user.id, photo=test4)    await bot.send_poll(        chat_id=call.from_user.id,        question=question,        options=answers,        is_anonymous=False,        type='quiz',        correct_option_id=0    )def register_handlers_callback(dp: Dispatcher):    dp.register_callback_query_handler(quiz_2, lambda call: call.data == 'button_call_1')    dp.register_callback_query_handler(quiz_3, lambda call: call.data == 'button_call_2')    dp.register_callback_query_handler(quiz_4, lambda call: call.data == 'button_call_3')