import asynciofrom aiogram import executorfrom my_bots.bot2.venv.config import dpfrom handlers import client, callback, extra, admin, fsm_anketa, notifications, inlineimport loggingfrom database.bot_db import sql_createasync def on_startup(_):    asyncio.create_task(notifications.sheduler())    sql_create()admin.register_handlers_admin(dp)client.register_handlers_client(dp)callback.register_handlers_callback(dp)fsm_anketa.register_handlers_fsm_anketa(dp)notifications.register_handlers_notifications(dp)inline.register_handler_inline(dp)#file in extra должен всегда быть последнимextra.register_handlers_extra(dp)'''запускаются все остальные файлы    если запустить этот файл'''if __name__ == '__main__':    logging.basicConfig(level=logging.INFO)    executor.start_polling(dp, skip_updates=True, on_startup=on_startup)